import UIKit
import Alamofire
import RealmSwift

protocol DisplayedObject {

}

enum ErrorType: LocalizedError {
  case InvalidConversionInput(message: String? = nil)
  case DatabaseRequestedObjectNotExisting(message: String? = nil)
  case UnsuportedCurrencyRequest(message: String? = nil)
  case InsufficientConvertingFunds(message: String)
}

extension ErrorType {
  var errorDescription: String? {
    switch self {
    case let .InsufficientConvertingFunds(message):
      return message
    case let .InvalidConversionInput(message):
      return message
    case let .DatabaseRequestedObjectNotExisting(message):
      return message
    case let .UnsuportedCurrencyRequest(message):
      return message
    }
  }
}

enum CurrencyConverter {
  // MARK: Use cases
  
  enum FetchCurrencyConversion {
    struct Request {
      var fromAmount: String
      var fromCurrency: String
      var toCurrency: String
    }

    struct Response {
      var conversion: ConvertedCurrency?
      var error: Error?
    }

    struct ViewModel {
      var receive: String
    }
  }

  enum FetchCurrencyConversionContract {
    struct Request {
      var fromAmount: String
      var fromCurrency: String
      var toCurrency: String
    }

    struct Response {
      var fromAmount: String?
      var totalAmount: Double?
      var fromCurrency: String?
      var toCurrency: String?
      var toAmount: String?
      var feeRate: Double?
      var fee: Double?
      var error: Error?
    }

    struct ViewModel {
      var message: String
    }
  }

  enum CompleteCurrencyConversionContract {
    struct Request {
      var fromAmount: String
      var fromCurrency: String
      var toCurrency: String
    }

    struct Response {
      var totalAmount: Double?
      var fromCurrency: String?
      var toCurrency: String?
      var toAmount: String?
      var fee: Double?
      var error: Error?
    }

    struct ViewModel {
      var message: String
    }
  }

  enum ErrorAlert {
