    }

    struct Response {
      var error: Error
    }

    struct ViewModel {
      var title: String
      var message: String
      var buttonTitle: String
    }
  }

  enum Something {
    struct Request
    {
    }

    struct Response
    {
    }

    struct ViewModel
    {
    }
  }

  enum CollectionView {
    enum CellType {
      case Balance
    }

    struct Request {
      var type: CellType
    }

    struct Response {
      struct BalanceCell {
        var objects: Results<StoredCurrency>?
